					{
						$first_post_text_hover = bbcode_strip($row['first_post_text_hover']);
						if (utf8_strlen($first_post_text_hover) >= $config['title_hover_limit'])
						{
							$first_post_text_hover = (utf8_strlen($first_post_text_hover) > $config['title_hover_limit'] + 3) ? utf8_substr($first_post_text_hover, 0, $config['title_hover_limit']) : $first_post_text_hover;
							// use last space before the character limit as the break-point, if one exists
							$new_char_limit = utf8_strrpos($first_post_text_hover, ' ') != false ? utf8_strrpos($first_post_text_hover, ' ') : $config['title_hover_limit'];
							$first_post_text_hover = utf8_substr($first_post_text_hover, 0, $new_char_limit) . '...';
						}
					}

